pragma solidity ^0.8.4;

import "./RegistryAuthFacet.sol";

library RegistryWhitelistAuthStorage {
    struct Layout {
        /**
         * trustedETHController and trustedReverseRegistrar has right to control any name regardless of approval
         * controller address => whitelisted
         */
        mapping(address => bool) whitelisted;
    }

    bytes32 internal constant STORAGE_SLOT =
        keccak256('optidomains.contracts.storage.RegistryWhitelistAuthStorage');

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

contract RegistryWhitelistAuthFacet is RegistryAuthFacet {
    event SetWhitelisted(address indexed operator, bool approved);

    function isAuthorised(address sender, bytes32 node) public virtual override view returns (bool) {
        return super.isAuthorised(sender, node) || RegistryWhitelistAuthStorage.layout().whitelisted[sender];
    }

    function setWhitelisted(address operator, bool approved) public baseOnlyOwner {
        RegistryWhitelistAuthStorage.Layout storage l = RegistryWhitelistAuthStorage
            .layout();
        l.whitelisted[operator] = approved;
        emit SetWhitelisted(operator, approved);
    }
}
","keccak256":"0x6d5467bbeaa4f5e4d57abda2a33da42acd337598cf24b5a4a4b387994765c808","license":"MIT"},"contracts/diamond-resolver/facets/base/DiamondResolverBaseInternal.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.8;

import "./DiamondResolverBaseStorage.sol";
import "./DiamondResolverUtil.sol";

error ERC165Base__InvalidInterfaceId();

abstract contract DiamondResolverBaseInternal is DiamondResolverUtil {
    // Logged when an operator is added or removed.
    event ApprovalForAll(
        address indexed owner,
        address indexed operator,
        bool approved
    );

    // Logged when a delegate is approved or an approval is revoked.
    event Approved(
        address owner,
        bytes32 indexed node,
        address indexed delegate,
        bool indexed approved
    );

    /**
     * @dev See {IERC1155-setApprovalForAll}.
     */
    function _setApprovalForAll(address operator, bool approved) internal {
        require(
            msg.sender != operator,
            "ERC1155: setting approval status for self"
        );

        DiamondResolverBaseStorage.Layout storage l = DiamondResolverBaseStorage
            .layout();
        l.operatorApprovals[msg.sender][operator] = approved;
        emit ApprovalForAll(msg.sender, operator, approved);
    }

    /**
     * @dev Approve a delegate to be able to updated records on a node.
     */
    function _approve(bytes32 node, address delegate, bool approved) internal {
        require(msg.sender != delegate, "Setting delegate status for self");

        DiamondResolverBaseStorage.Layout storage l = DiamondResolverBaseStorage
            .layout();
        l.tokenApprovals[msg.sender][node][delegate] = approved;
        emit Approved(msg.sender, node, delegate, approved);
    }

    /**
     * @dev See {IERC1155-isApprovedForAll}.
     */
    function _isApprovedForAll(
        address account,
        address operator
    ) internal view returns (bool) {
        DiamondResolverBaseStorage.Layout storage l = DiamondResolverBaseStorage
            .layout();
        return l.operatorApprovals[account][operator];
    }

    /**
     * @dev Check to see if the delegate has been approved by the owner for the node.
     */
    function _isApprovedFor(
        address owner,
        bytes32 node,
        address delegate
    ) internal view returns (bool) {
        DiamondResolverBaseStorage.Layout storage l = DiamondResolverBaseStorage
            .layout();
        return l.tokenApprovals[owner][node][delegate];
    }
}
