        address owner = registry.ens().owner(node);
        if (registry.isNameWrapper(owner)) {
            owner = INameWrapper(owner).ownerOf(uint256(node));
        }

        return
            owner == sender ||
            _isApprovedForAll(owner, sender) ||
            _isApprovedFor(owner, node, sender);
    }
}
