//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17 <0.9.0;

import "./SolidStateDiamond.sol";
import "./DiamondResolverOverrider.sol";
import "./Multicallable.sol";
import "./IDiamondResolver.sol";
import "./facets/base/IDiamondResolverBase.sol";
import "./facets/base/DiamondResolverBase.sol";
import "../registry/ENS.sol";
import "./INameWrapperRegistry.sol";
import {IReverseRegistrar} from "../reverseRegistrar/IReverseRegistrar.sol";
import {ReverseClaimer} from "../reverseRegistrar/ReverseClaimer.sol";
import {INameWrapper} from "../wrapper/INameWrapper.sol";

bytes4 constant supportsInterfaceSignature = 0x01ffc9a7;

contract DiamondResolver is 
    SolidStateDiamond,
    Multicallable,
    DiamondResolverBase
{
    bytes32 constant ADDR_REVERSE_NODE =
        0x91d1777781884d03a6757a803996e38de2a42967fb37eeaca72729271025a9e2;

    INameWrapperRegistry public immutable registry;

    constructor(address _owner, INameWrapperRegistry _registry) SolidStateDiamond(_owner) {
        registry = _registry;
    }

    function initialize(address _owner, address _fallback) public virtual override {
        super.initialize(_owner, _fallback);

        if (_fallback == address(0)) {
            bytes4[] memory selectors = new bytes4[](8);
            uint256 selectorIndex;

            // register DiamondResolverBase

            selectors[selectorIndex++] = IHasNameWrapperRegistry.registry.selector;
            selectors[selectorIndex++] = IDiamondResolverBase.setApprovalForAll.selector;
            selectors[selectorIndex++] = IDiamondResolverBase.isApprovedForAll.selector;
            selectors[selectorIndex++] = IDiamondResolverBase.approve.selector;
            selectors[selectorIndex++] = IDiamondResolverBase.isApprovedFor.selector;
            selectors[selectorIndex++] = IVersionableResolver.recordVersions.selector;
            selectors[selectorIndex++] = IVersionableResolver.clearRecords.selector;
            selectors[selectorIndex++] = IDiamondResolverFactory.clone.selector;

            // diamond cut

            FacetCut[] memory facetCuts = new FacetCut[](1);

            facetCuts[0] = FacetCut({
                target: address(this),
                action: FacetCutAction.ADD,
                selectors: selectors
            });

            _diamondCut(facetCuts, address(0), '');
        }

        _setSupportsInterface(type(IDiamondResolver).interfaceId, true);
        _setSupportsInterface(type(IVersionableResolver).interfaceId, true);
        _setSupportsInterface(type(IHasNameWrapperRegistry).interfaceId, true);
        _setSupportsInterface(type(IDiamondResolverFactory).interfaceId, true);
    }

    function supportsInterface(
        bytes4 interfaceID
    )
        public
        view
        virtual
        override(Multicallable, SolidStateDiamond)
        returns (bool)
    {
        return SolidStateDiamond.supportsInterface(interfaceID) || Multicallable.supportsInterface(interfaceID);
    }
}

//SPDX-License-Identifier: MIT
pragma solidity >=0.8.17 <0.9.0;

import "./facets/base/DiamondResolverUtil.sol";

error Overrider_SignatureBlacklisted(bytes4 sig);
error Overrider_ImplementationNotWhitelisted(address implementation);

library DiamondResolverOverriderStorage {
    struct Layout {
        // Facets overriding mapping
        mapping(bytes32 => mapping(bytes4 => address)) facets;

        // Blacklisted function signature
        mapping(bytes4 => bool) blacklisted;

        // Whitelisted implementation address
        mapping(address => bool) whitelisted;
    }

    bytes32 internal constant STORAGE_SLOT =
        keccak256(
            "optidomains.contracts.storage.DiamondResolverOverriderStorage"
        );

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

contract DiamondResolverOverrider is DiamondResolverUtil {
    function _getImplementation()
        internal
        view
        virtual
        returns (address)
    {
        if (msg.data.length < 36) {
            return address(0);
        }

        // inline storage layout retrieval uses less gas
        DiamondResolverOverriderStorage.Layout storage l;
        bytes32 slot = DiamondResolverOverriderStorage.STORAGE_SLOT;
        assembly {
            l.slot := slot
        }

        if (l.blacklisted[msg.sig]) {
            return address(0);
        }

        return l.facets[bytes32(msg.data[4:36])][msg.sig];
    }

    function setOverrideBlacklist(bytes4[] memory sig, bool blacklisted) public baseOnlyOwner {
        // inline storage layout retrieval uses less gas
        DiamondResolverOverriderStorage.Layout storage l;
        bytes32 slot = DiamondResolverOverriderStorage.STORAGE_SLOT;
        assembly {
            l.slot := slot
        }

        unchecked {
            uint256 sigLength = sig.length;
            for (uint256 i; i < sigLength; ++i) {
                l.blacklisted[sig[i]] = blacklisted;
            }
        }
    }

    function setWhitelistedImplementation(address[] memory implementations, bool whitelisted) public baseOnlyOwner {
        // inline storage layout retrieval uses less gas
        DiamondResolverOverriderStorage.Layout storage l;
        bytes32 slot = DiamondResolverOverriderStorage.STORAGE_SLOT;
        assembly {
            l.slot := slot
        }

        unchecked {
            uint256 implLength = implementations.length;
            for (uint256 i; i < implLength; ++i) {
                l.whitelisted[implementations[i]] = whitelisted;
            }
        }
    }

    function setOverride(bytes32 node, bytes4 sig, address implementation) public authorised(node) {
        // inline storage layout retrieval uses less gas
        DiamondResolverOverriderStorage.Layout storage l;
        bytes32 slot = DiamondResolverOverriderStorage.STORAGE_SLOT;
        assembly {
            l.slot := slot
        }

        if (l.blacklisted[sig]) {
            revert Overrider_SignatureBlacklisted(sig);
        }

        if (!l.whitelisted[implementation]) {
            revert Overrider_ImplementationNotWhitelisted(implementation);
        }

        l.facets[node][sig] = implementation;
    }
}

