// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

import "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import "./profiles/IVersionableResolver.sol";

abstract contract ResolverBase is ERC165, IVersionableResolver {
    mapping(bytes32 => uint64) public recordVersions;

    function isAuthorised(bytes32 node) internal view virtual returns (bool);

    modifier authorised(bytes32 node) {
        require(isAuthorised(node));
        _;
    }

    /**
     * Increments the record version associated with an ENS node.
     * May only be called by the owner of that node in the ENS registry.
     * @param node The node to update.
     */
    function clearRecords(bytes32 node) public virtual authorised(node) {
        recordVersions[node]++;
        emit VersionChanged(node, recordVersions[node]);
    }

    function supportsInterface(
        bytes4 interfaceID
    ) public view virtual override returns (bool) {
        return
            interfaceID == type(IVersionableResolver).interfaceId ||
            super.supportsInterface(interfaceID);
    }
}

contracts/root/Controllable.sol.content:
pragma solidity ^0.8.4;

import "./Ownable.sol";

bytes32 constant TOPIC_SET_CONTROLLER = keccak256("setController");

abstract contract Controllable is Ownable {
    mapping(address => bool) public controllers;

    event ControllerChanged(address indexed controller, bool enabled);

    modifier onlyController() {
        require(
            controllers[msg.sender],
            "Controllable: Caller is not a controller"
        );
        _;
    }

    function setController(
        address controller,
        bool enabled,
        bytes memory signature
    ) public {
        if (
            !verifyOwnerSignature(
                TOPIC_SET_CONTROLLER,
                keccak256(abi.encodePacked(controller, enabled)),
                signature
            )
        ) {
            revert InvalidOperatorSignature();
        }

        controllers[controller] = enabled;
        emit ControllerChanged(controller, enabled);
    }
}

contracts/root/Ownable.sol.content:
pragma solidity ^0.8.4;

import {SignatureChecker} from "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";

bytes32 constant TOPIC_TRANSFER_OWNERSHIP = keccak256("transferOwnership");

error InvalidOperatorSignature();

contract Ownable {
    address public owner;
    uint256 public currentNonce;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    modifier onlyOwner() {
        require(isOwner(msg.sender));
        _;
    }

    constructor(address _owner) public {
        owner = _owner;
    }

    function verifyOwnerSignature(
        bytes32 topic,
        bytes32 digest,
        bytes memory signature
    ) internal returns (bool) {
        return
            SignatureChecker.isValidSignatureNow(
                owner,
                keccak256(
                    abi.encodePacked(
                        bytes1(0x19),
                        bytes1(0),
                        address(this),
                        topic, // keccak256("...")
                        currentNonce++,
                        digest
                    )
                ),
                signature
            );
    }

    function transferOwnership(
        address newOwner,
        bytes memory signature
    ) public {
        if (
            !verifyOwnerSignature(
                TOPIC_TRANSFER_OWNERSHIP,
                keccak256(abi.encodePacked(newOwner)),
                signature
            )
        ) {
            revert InvalidOperatorSignature();
        }

        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }

    function isOwner(address addr) public view returns (bool) {
        return owner == addr;
    }
}

contracts/root/Root.sol.content:
pragma solidity ^0.8.4;

import "../registry/ENS.sol";
import "./Controllable.sol";

bytes32 constant TOPIC_LOCK = keccak256("lock");
bytes32 constant TOPIC_EXECUTE = keccak256("execute");

contract Root is Controllable {
    bytes32 private constant ROOT_NODE = bytes32(0);

    bytes4 private constant INTERFACE_META_ID =
        bytes4(keccak256("supportsInterface(bytes4)"));

    event TLDLocked(bytes32 indexed label);

    mapping(bytes32 => bool) public locked;

    constructor(address _owner) Ownable(_owner) {}

    function setSubnodeOwner(
        ENS ens,
        bytes32 label,
        address owner
    ) external onlyController {
        require(!locked[label]);
        ens.setSubnodeOwner(ROOT_NODE, label, owner);
    }

    event RootExecution(address indexed target, bytes data, bytes result);

    function execute(
        address target,
        bytes memory data,
        bytes memory signature
    ) external returns (bytes memory) {
        if (
            !verifyOwnerSignature(
                TOPIC_EXECUTE,
                keccak256(abi.encodePacked(target, data)),
                signature
            )
        ) {
            revert InvalidOperatorSignature();
        }

        (bool success, bytes memory result) = target.call(data);

        if (!success) {
            assembly {
                revert(add(result, 32), mload(result))
            }
        }

        emit RootExecution(target, data, result);

        return result;
    }

    function lock(bytes32 label, bytes memory signature) external {
        if (!verifyOwnerSignature(TOPIC_LOCK, label, signature)) {
            revert InvalidOperatorSignature();
        }

        emit TLDLocked(label);
        locked[label] = true;
    }

    function supportsInterface(
        bytes4 interfaceID
    ) external pure returns (bool) {
        return interfaceID == INTERFACE_META_ID;
    }
}

contracts/utils/HexUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

library HexUtils {
    /**
     * @dev Attempts to parse bytes32 from a hex string
     * @param str The string to parse
     * @param idx The offset to start parsing at
     * @param lastIdx The (exclusive) last index in `str` to consider. Use `str.length` to scan the whole string.
     */
    function hexStringToBytes32(
        bytes memory str,
        uint256 idx,
        uint256 lastIdx
    ) internal pure returns (bytes32 r, bool valid) {
        valid = true;
        assembly {
            // check that the index to read to is not past the end of the string
            if gt(lastIdx, mload(str)) {
                revert(0, 0)
            }

            function getHex(c) -> ascii {
                // chars 48-57: 0-9
                if and(gt(c, 47), lt(c, 58)) {
                    ascii := sub(c, 48)
                    leave
                }
                // chars 65-70: A-F
                if and(gt(c, 64), lt(c, 71)) {
                    ascii := add(sub(c, 65), 10)
                    leave
                }
                // chars 97-102: a-f
                if and(gt(c, 96), lt(c, 103)) {
                    ascii := add(sub(c, 97), 10)
                    leave
                }
                // invalid char
                ascii := 0xff
            }

            let ptr := add(str, 32)
            for {
                let i := idx
            } lt(i, lastIdx) {
                i := add(i, 2)
            } {
                let byte1 := getHex(byte(0, mload(add(ptr, i))))
                let byte2 := getHex(byte(0, mload(add(ptr, add(i, 1)))))
                // if either byte is invalid, set invalid and break loop
                if or(eq(byte1, 0xff), eq(byte2, 0xff)) {
                    valid := false
                    break
                }
                let combined := or(shl(4, byte1), byte2)
                r := or(shl(8, r), combined)
            }
        }
    }

    /**
     * @dev Attempts to parse an address from a hex string
     * @param str The string to parse
     * @param idx The offset to start parsing at
     * @param lastIdx The (exclusive) last index in `str` to consider. Use `str.length` to scan the whole string.
     */
    function hexToAddress(
        bytes memory str,
        uint256 idx,
        uint256 lastIdx
    ) internal pure returns (address, bool) {
        if (lastIdx - idx < 40) return (address(0x0), false);
        (bytes32 r, bool valid) = hexStringToBytes32(str, idx, lastIdx);
        return (address(uint160(uint256(r))), valid);
    }
}
